import React from "react";
import {Form, Card, Col, Button} from "react-bootstrap";
import {ip} from "./config/config.json"
import axios from "axios";
import Avatar from "react-avatar";
import "./css/Login.css";

class UserAdmin extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            isLoaded:false,
            userId: '',
            roleName:"USER",
            user: [],

        };


        this.onChangeRoleName = this.onChangeRoleName.bind(this)
    }

    onChangeRoleName(event) {
        this.setState({ roleName: event.target.roleName })

    }

    componentDidMount() {
        axios({
            method:'get',
            url:'http://'+ip+':8080/getUsersById?userId='+this.props.match.params.userId,
        }).then(response => response.data)
            .then((data) =>{
                this.setState({user: data});
                this.setState({isLoaded: true});

            });
    }


    render() {
        if(this.state.isLoaded===true)
            return (

                <Card className={"border border-light bg-light text-black"}>
                   <Card.Header>
                       <Avatar name={this.state.user.login} size="50" round={true}></Avatar>
                       <b className={"login"}>{this.state.user.login}</b>
                   </Card.Header>
                    <Card.Body>
                        {'HEAD_ADMIN' === localStorage.getItem('typeOfUser')?
                        <div>
                            {this.state.user.role.roleName==='HEAD_ADMIN' ?
                                <div>
                                    <b>Nie można nic zrobić. Wszelkie zmiany muszą zostać wykoane
                                        z posiomu serwara bazy danych.</b>
                                </div>
                            :
                                <div>
                                    <Form onSubmitRole={this.submitStart}>
                                        <Form.Row>
                                            <Form.Group as={Col}>
                                                <Form.Label>Od ilu lat</Form.Label>
                                                <Form.Control
                                                    as="select"
                                                    required
                                                    autoComplete={"off"}
                                                    name={"roleName"}
                                                    value={this.state.roleName}
                                                    onChange={this.onChangeRoleName}
                                                    className={"bg-light text-black"}
                                                >
                                                    <option value = {"USER"}>USER</option>
                                                    <option value = {"MODERATOR"}>MODERATOR</option>
                                                    <option value = {"ADMIN"}>ADMIN</option>
                                                </Form.Control>
                                            </Form.Group>
                                            <Button size={"sm"} variant="success" type="submit" style={{"textAlign":"center"}}>
                                                Zmień
                                            </Button>
                                        </Form.Row>
                                    </Form>
                                </div>
                            }

                        </div>
                            :
                            <div>

                            </div>
                        }
                    </Card.Body>

                </Card>

            );else return(<h1>Loading!</h1>)


    }

}


export default UserAdmin;
